# OEIS sequence: A261573

def sequence(n, k):
    """For n > 0 and k >= 0, generates the first n terms of the sequence"""
    A, a = {0}, 0
    yield a
    for n in range(1, n + 1):
        a = a - (n + k)
        if a > 0 and a not in A:
            A.add(a)
            yield a
        else:
            a = a + 2 * (n + k)
            A.add(a)
            yield a
# List of the first 1000 terms of the sequence with k = 2.
list(sequence(1000, 2))

