# A274369: Langton's ant by _Andrey Zabolotskiy_, Jul 05 2016
def ant(n):
    steps = [(1, 0), (0, 1), (-1, 0), (0, -1)]
    black = set()
    x = y = 0
    position = [(x, y)]
    direction = 2
    for _ in range(n):
        if (x, y) in black:
            black.remove((x, y))
            direction += 1
        else:
            black.add((x, y))
            direction -= 1
        (dx, dy) = steps[direction%4]
        x += dx
        y += dy
        position.append((x, y))
    return position
print([p[0] for p in ant(100)])
# change p[0] to p[1] to get y-coordinates

