def unary(n):
    return "1"*(n-1)+"0"
def elias_gamma(n):
    if n ==1:
        return "1"
    k=int(math.log(n,2))
    fp=unary(1+k)    #fp is the first part
    sp=n-2**(k)      #sp is the second part
    nb=k             #nb is the number of bits used to store sp in binary
    sp=bin(sp)[2:]
    if len(sp)<nb:
        sp=("0"*(nb-len(sp)))+sp
    return fp+sp

