from sympy import factorial, factorint
def T(n, m): return ((n + m)**2 - n - 3*m + 2)/2
def P(n):
    f = factorint(n)
    return sorted([f[i] for i in f])
def a046523(n):
    x=1
    while True:
        if P(n) == P(x): return x
        else: x+=1
def a055881(n):
    m = 1
    while n%factorial(m)==0:
        m+=1
    return m - 1
def a(n): return T(a055881(n), a046523(n)) # _Indranil Ghosh_, May 05 2017

