from sympy import factorint, primepi
def f(n):
    return 0 if n < 2 else sum(1 << int(primepi(i-1)) for i in factorint(n))
def a(n):
    fn, c = n, 0
    while not fn == 0: fn, c = f(fn), c+1
    return c
print([a(n) for n in range(1, 38)]) # _Michael S. Branicky_, Jul 11 2022

