from sympy import isprime
def a(n):
  A, p, Ap = 3, 2**n, 3**(2**n)
  while True:
    if any(isprime((Ap + B**p)//2) for B in range(1, A, 2)): return A
    A += 2; Ap = A**p
print([a(n) for n in range(10)]) # _Michael S. Branicky_, Mar 03 2021

