# OEIS sequence: A306727

def aseq(p, x, k):
    # generic algorithm for any p - power base, p=3 for this sequence
    if x < 0:
        return 0
    if x < p:
        return 1
    # coefficients
    arr = [0]*(x+1)
    arr[0] = 1
    m = p**k
    while m > 0:
        for i in range(m, x+1, m):
            arr[i] += arr[i-m]
        m //= p
    return arr[x]
def A(n, k):
    p = 3
    return aseq(p, p*n, k)
# A(n, k), 5 = A(3, 3) = aseq(3, 3*3, 3)
