from sympy import nextprime
def A308438(n):
    l, p = 1, nextprime(n)
    while True:
        q = nextprime(p)
        if q-p == 2*n:
            return p
        p = q
        if p >= (n+1)*l:
            l *= 10
            p = nextprime(n*l) # _Chai Wah Wu_, May 31 2019

