# OEIS sequence: A337239

def A337239(n):
    def f(x):
        s = oct(x)[2:]
        l = s.find('7')
        if l >= 0:
            s = s[:l]+'6'*(len(s)-l)
        return n+int(s,7)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
    return m # _Chai Wah Wu_, Dec 04 2024

