from gmpy2 import digits
def A337250(n):
    def f(x):
        l = (s:=digits(x,4)).find('3')
        if l >= 0: s = s[:l]+'2'*(len(s)-l)
        return n+int(s,3)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
    return m # _Chai Wah Wu_, Dec 04 2024

