from sympy import isprime, nextprime
def a(n, startp=2):
    p = startp
    while any(isprime(2*p+i) for i in range(1, 2*n+2, 2)): p = nextprime(p)
    return p
print([a(n) for n in range(41)]) # _Michael S. Branicky_, Jul 31 2021

