from sympy import primerange
def aupto(limit):
  ep = [p for p in primerange(1, limit)][1::2]
  ss = sorted(set(p1 + p2 for i, p1 in enumerate(ep) for p2 in ep[i:]))
  return [s for s in ss if s <= limit]
