from sympy import isprime
def a(n):
    ending = 2*n - 1 + (n+1)//4 * 2 # A045572
    i, pow10 = ending, 10**len(str(ending))
    while not isprime(i): i += pow10
    return i
print([a(n) for n in range(1, 64)]) # _Michael S. Branicky_, Nov 03 2021

