# OEIS sequence: A340836

def A340836(n):
    if n == 0:
        return 0
    s = bin(n)[2:]
    i = s.find('0')
    if i == -1:
        return n
    s1, s2 = s[:i+1], s[i+1:]
    if s2 == '':
        return n+1
    if int(s2) <= 1:
        return int('1'+s2[-2::-1]+s1[::-1],2)
    else:
        return int('1'+'0'*(len(s2)-1)+bin(int(s1,2)+1)[:1:-1],2) # _Chai Wah Wu_, Mar 14 2021

