def reverse(n):
    s = 0
    while n > 0:
        d, n = n%10, n//10
        s = 10*s+d
    return s
def A341707(n):
    s, t = 0, 1
    while n > 0:
        b, n = n%2, n//2
        if n > 0:
            s, t = reverse(t*b)+s, 2*t
        else:
            s = reverse(t*b)-s
    return s # _A.H.M. Smeets_, Feb 18 2021

