from sympy.utilities.iterables import multiset_permutations as mp
from itertools import count, islice, combinations_with_replacement as mc
def c(s):
    d = list(map(int, s))
    return prod(d) == 2*len(d)*sum(d)
def agen():
    for d in count(2):
        okset = set()
