def is_nondec(n, b):
  if n < b: return True
  n, r = divmod(n, b)
  while n >= b:
    (n, r), lastd = divmod(n, b), r
    if r < lastd: return False
  return n >= r
def a(n):
  for b in range(2, n+3):
    if is_nondec(n, b): return b
