from sympy import isprime
def ok(n):
    if n%10 not in {1, 3, 7, 9}: return False
    digs = str(n)
    if int(max(digs)) != len(digs): return False
    for j, dj in enumerate(digs, start=1):
        if dj != '0' and digs[int(dj)-1] != str(j): return False
    return isprime(n)
