from sympy import isprime, primerange
def ok(p):
    s, b = str(p), bin(p)[2:]
    if s == s[::-1] or b == b[::-1]: return False
    return isprime(int(s[::-1])) and isprime(int(b[::-1], 2))
print(list(filter(ok, primerange(1, 3720)))) # _Michael S. Branicky_, Aug 24 2021

