from sympy import factorint
def aupto(limit):
    alst, condvec = [], [False, False, False]
    for kp2 in range(4, limit+3):
        evenodd = [0, 0]
        for e in factorint(kp2).values():
            evenodd[e%2] += 1
        condvec = condvec[1:] + [evenodd[0] == evenodd[1]]
        if all(condvec):
            alst.append(kp2-2)
    return alst
