from itertools import count, takewhile
def ok(n):
  s = str(n*n); return len(s.rstrip("4")) == len(s.lstrip("4")) == len(s)-1
def aupto(N):
  r = takewhile(lambda x: x<=N, (10*i+d for i in count(0) for d in [2, 8]))
  return [k for k in r if ok(k)]
