from itertools import permutations
def ps(lst):  # pop-stack sorting operator [cf. Claesson, Gu√∞mundsson]
    out, stack = [], []
    for i in range(len(lst)):
        if len(stack) == 0 or stack[-1] < lst[i]:
            out.extend(stack[::-1])
            stack = []
        stack.append(lst[i])
    return out + stack[::-1]
def psops(t):
    c, lst, srtdlst = 0, list(t), sorted(t)
    if lst == srtdlst: return 0
    while lst != srtdlst:
        lst = ps(lst)
        c += 1
    return c
def a(n):
    return sum(1 for p in permutations(range(n), n) if psops(p) == n-1)
