from sympy import prevprime, factorint, prod
def f(p, e):
    return e+1 if p == 2 else prevprime(p)**e
def a(n):
    return prod(f(p, e) for p, e in factorint(n).items()) # _Sebastian Karlsson_, Nov 15 2021

