from itertools import product
def a(n):
    if n == 0: return 1
    nn = ["0"] + [str(i)+"**2" for i in range(1, n+1)]
    return sum(eval("".join([*sum(zip(nn, ops+("", )), ())])) in {0, 1} for ops in product("+-", repeat=n))
print([a(n) for n in range(18)]) # _Michael S. Branicky_, Jan 16 2022

