from sympy import isprime, nextprime
def c(p, q, r): return isprime(p+q-r) and isprime(p-q+r) and isprime(-p+q+r)
def afind():
    p, q, r = 2, 3, 5
    while True:
        if c(p, q, r): print(p, end=", ")
        p, q, r = q, r, nextprime(r)
