from itertools import islice
def c(s, t): return sum(t.count(si) for si in s)
def agen(): # generator of terms
    an, target, seen, mink = 1, "1", {1}, 2
    while True:
        yield an
        k = mink
        while k in seen or c(str(k), target) != 3: k += 1
