def is_pow(n, k):
    while n%k == 0: n = n//k
    return n == 1
def any_power(n):
    return any((is_pow(n, k) for k in range(2,1+n//2)))
terms,s,sums = [2,], set((2,)), set((2,))
for i in range(100):
    t = 3
    while t in s or any_power(t) or any((any_power(j + t) for j in sums)):
        t+=1
    s.add(t);terms.append(t)
    sums = set(map(lambda k:k+t, sums))
    sums.add(t)
