 # faster version for generating initial segment of sequence
from itertools import combinations, count, islice
def agen():
    for d in count(4):
        b, c = 2**d - 1, 2**(d-1)
        for i, j in combinations(range(1, d-1), 2):
            yield b - (c >> i) - (c >> j)
print(list(islice(agen(), 60))) # _Michael S. Branicky_, Oct 13 2022
from math import comb, isqrt
from sympy import integer_nthroot
def A357773(n):
    a = (m:=integer_nthroot(6*n, 3)[0])+(n>comb(m+2,3))+3
    b = isqrt((j:=comb(a-1,3)-n+1)<<3)+3>>1
    c = j-comb((r:=isqrt(w:=j<<1))+(w>r*(r+1)),2)
    return (1<<a)-(1<<b)-(1<<c)-1 # _Chai Wah Wu_, Dec 17 2024

