from itertools import count, islice
def f(n): return 1 if n == 0 else (m:=3*n)^((1 << m.bit_length())-1)
def iters(n):
    i, fi = 0, n
    while fi != 0: i, fi = i+1, f(fi)
    return i
def agen(): # generator of terms
    record = -1
    for m in count(0):
        v = iters(m)
        if v > record: yield v; record = v
