from itertools import islice
from sympy import isprime, nextprime
def agen(): # generator of terms
    s = 2; yield 2
    while True:
        p = 2
        while True:
            if isprime(s*p-1) and isprime(s*p+1):
                yield p; s *= p; break
            p = nextprime(p)
print(list(islice(agen(), 55))) # _Michael S. Branicky_, Jan 19 2023

