from sympy import sieve
def a(n):
    pn = p = sieve[n]; res = dict(); i = 0
    while True:
        if p not in res:
            res[p] = {0} | {q%p for q in res}
        for q in res:
            res[q].add(p%q)
            if len(res[q]) == q:
                return p
        i += 1
        p = sieve[n+i]
print([a(n) for n in range(1, 54)]) # _Michael S. Branicky_, Feb 02 2023

