from sympy.ntheory import digits
def a(n): return int("".join("".join(map(str, digits(k, 3)[1:])) for k in range(n, 0, -1)), 3)
print([a(n) for n in range(1, 21)]) # _Michael S. Branicky_, Feb 19 2023
