def Pn(n,s): # probability numerator of rolling s with an n-sided die
    if s==0: return 1
    elif s>n: return int((n+1)**(s-n-2)*(n*(n+1)**n-n**n*s+(n+1)**n))
    else: return (n+1)**(s-1)
