from itertools import islice
from sympy.ntheory.factor_ import digits
def agen(): # generator of terms
    an, y = 1, 1
    while y < 8:
        yield an
        an, y = an + 8*(an%8), 1
        while y < 8:
            if str(digits(an+y,8)[1]) == str(y):
                an += y
                break
            y += 1
print(list(islice(agen(), 52))) # _Michael S. Branicky_, Nov 16 2023

