from itertools import islice
from sympy.ntheory.factor_ import digits
def agen(b=16): # generator of terms
    an, y = 1, 1
    while y < b:
        yield an
        an, y = an + b*(an%b), 1
        while y < b:
            if str(digits(an+y, b)[1]) == str(y):
                an += y
                break
            y += 1
print(list(islice(agen(), 50))) # _Michael S. Branicky_, Nov 16 2023

