from collections import Counter
def A369709(n):
    c = {0:1}
    for k in range(1,n+1):
        d = Counter(c)
        for j in c:
            a = c[j]
            d[j+k] += 3*a
            d[j+2*k] += 3*a
            d[j+3*k] += a
        c = d
    return max(c.values()) # _Chai Wah Wu_, Feb 07 2024

