# OEIS sequence: A369775

from collections import Counter
from sympy import prime, primerange
def A369775(n):
    if n == 0: return 1
    c, p = {0:1}, list(primerange(prime(n)+1))
    for k in range(1,n+1):
        d = Counter(c)
        for j in c:
            a = c[j]
            for i in p[:k]:
                d[j+i] += a
        c = d
    return max(c.values()) # _Chai Wah Wu_, Feb 01 2024

