from itertools import islice
def agen(): # generator of terms
    s, a = set(), []
    while True:
        an, allnew = 0, False
        while not allnew:
            allnew, an, sn = True, an+1, set()
            for i in range(len(a)):
                if an == a[i]:
                    t = sum(a[i+1:]) + 2*an
                    if t in s or t in sn: allnew = False; break
                    sn.add(t)
        yield an; a.append(an); s |= sn
