from num2words import num2words
from itertools import count, islice
def name(n): return num2words(n).replace(" and","")
def agen(): # generator of terms
    an, aset, mink = 1, set(), 1
    while True:
        yield an
        aset.add(an)
        t1, t2, k = str(an)[0], name(an)[0], mink
