from itertools import groupby
def a(n):
    r = [len(list(g)) for k, g in groupby(bin(n)[2:])]
    return int("1"*r[0]+"".join(str(i&1)*abs(r[i+1]-r[i]) for i in range(len(r)-1)), 2)
print([a(n) for n in range(1, 64)]) # _Michael S. Branicky_, Apr 10 2024

