def primeTest(num):
    ans = 0
    if num == 1 or num == 2:
        return num
    for i in range(0,int(num**0.5)):
        if (num/(i+2)).is_integer() == True:
            ans = num/(i+2)
            break
    if ans == 0:
        ans = num
    return ans
def seqgen(start):
    seq = start
