# faster for initial segment of sequence
from sympy import nextprime
from itertools import count, islice
def agen(): # generator of terms
    pn = 2
    for n in count(1):
        t = str(pn)
        yield next(k for k in count(1) if t in str(n*k))
        pn = nextprime(pn)
