from sympy import isprime
def a(n):
    if n == 1: return 23
    lb, ub = 10**(n-1), 10**n
    for k in range(lb, ub, 2):
        base = k*ub
        for inc in [k-1, k+1]:
            if inc >= lb and isprime(t:=base+inc):
                return t
print([a(n) for n in range(1, 16)]) # _Michael S. Branicky_, May 19 2024

