from itertools import count, islice
from sympy import sieve, factorint
def a_gen():
    for n in count(1):
        b = sum((i+1) for i, x in enumerate(bin(n)[2:][::-1]) if x =='1')
        p = sum(sieve.search(i)[0] for i in factorint(n, multiple=True))
        yield(b-p)
