# OEIS sequence: A374176

def a(n):
    b, c, m = bin(n)[2:], 0, 0
    for i in range(len(b)-1):
        if b[i] != b[i+1]: c += 1
        else: m = max(m, c); c = 0
    return max(m, c)
print([a(n) for n in range(1, 89)]) # _Michael S. Branicky_, Jul 06 2024
