from itertools import islice
from sympy import prime, composite, isprime
def agen(): # generator of terms
    an = 3
    while True:
        yield an
        if isprime(abs(an)):
            an = an - prime(abs(an))
        else:
            an += composite(abs(an)-1) if abs(an) > 1 else 1
print(list(islice(agen(), 38))) # _Michael S. Branicky_, Jul 22 2024

