# faster for initial segment of sequence
from sympy import isprime
from itertools import count, islice
def agen(): # generator of terms
    pic, prevc = 0, -1
    for i in count(4):
        if not isprime(i):
            if i == prevc + 1:
                yield pic
            pic, prevc = pic+1, i
print(list(islice(agen(), 10000))) # _Michael S. Branicky_, Sep 17 2024

