from itertools import islice
from collections import Counter
from sympy import isprime, nextprime, prevprime
def agen(verbose=False): # generator of terms; verbose=True prints rows
    seed, bigp = [2, 2], 2
    c = Counter(seed)
    yield from seed
    if verbose: [print(k, end=",\n") for k in seed]
    while True:
        p = 2
        while p <= bigp:
            cp = c[p]
            if cp > 2 and not isprime(cp): cp = prevprime(cp)
            if cp > bigp: bigp = cp
            yield cp
            if verbose: print(cp, end=", ")
            c[cp] += 1
            p = nextprime(p)
        if verbose: print()
print(list(islice(agen(), 94))) # _Michael S. Branicky_, Sep 10 2024

