from sympy import isprime
from itertools import islice
def agen(): # generator of terms
    a = [0, 1]
    yield from a
    while True:
        an = a[-1]+a[-2] if a[-1] < 3 or not isprime(a[-1]) else a[-1]-a[-2]
        yield an
        a = [a[-1], an]
print(list(islice(agen(), 50))) # _Michael S. Branicky_, Oct 11 2024

