from sympy.ntheory import n_order
def A377280(n):
    modular = 2*n + 1
    order = n_order(2, 2*n+1)
    if order % 2 == 0 and pow(2, order//2, modular) == modular - 1:
        return (order//2) * n
    else:
        return order * n # after _Kevin Ryde_

